mapper.select:
		select * from 
  		(select rownum as rnum, n.* from 
      		(select 
          		notice_no, title, write_date
     			 from notice order by notice_no desc
     			 )n
     		)
     	where rnum between #{start} and #{end}

select news_no, count(*) as LIKE_COUNT from news_like 
	group by news_no
	having news_no = #{newsNo}
