mapper.select:
  	select * from 
    		(select rownum as rnum, n.* from 
        		(select 
            		* 
       			 from product_tbl order by product_no desc
       			 )n
       		)
		where rnum between #{start} and #{end}

  	 	select * from 
    		(select rownum as rnum, n.* from 
        		(select * from product_tbl order by product_price asc
       			 )n
       		)
		where rnum between #{start} and #{end}

  	select * from 
    		(select rownum as rnum, n.* from 
        		(select 
            		* 
       			 from product_tbl where product_title ='dessert' order by product_no desc
       			 )n
       		)
		where rnum between #{start} and #{end}

	select * from
  (select rownum as rnum, n.* from 
              (select *
                  from product_tbl p
                  left join product_best_tbl b on (b.product_no = p.product_no)
                  order by product_best_no asc
              )n
      )
      where rnum between #{start} and #{end}
